mapper.update:
update clazz
name = #{name}, room = #{room}, begin_date = #{beginDate}, end_date = #{endDate}, master_id = #{masterId}, subject = #{subject}
where id = #{id}

mapper.select:
select c.*, e.name as masterName from clazz c left join emp e on e.id = c.master_id


        c.name like concat('%', #{name}, '%')


        and c.begin_date >= #{begin}


        and c.end_date <= #{end}


order by c.update_time desc
